package io.opensphere.core.cache.jdbc;

/**
 * Container for column name constants.
 */
public final class ColumnNames
{
    /** Category column name. */
    public static final String CATEGORY = "CATEGORY";

    /** Creation time column name. */
    public static final String CREATION_TIME = "CREATION_TIME";

    /** Critical column name. */
    public static final String CRITICAL = "CRITICAL";

    /** Data id column identifier. */
    public static final String DATA_ID = "DATA_ID";

    /** Expiration time column name. */
    public static final String EXPIRATION_TIME = "EXPIRATION_TIME";

    /** Family column name. */
    public static final String FAMILY = "FAMILY";

    /** Group id column identifier. */
    public static final String GROUP_ID = "GROUP_ID";

    /** Join id column identifier. */
    public static final String JOIN_ID = "JOIN_ID";

    /** Property column name. */
    public static final String PROPERTY = "PROPERTY";

    /** Sequence column identifier. */
    public static final String SEQUENCE = "SEQUENCE";

    /** Source column name. */
    public static final String SOURCE = "SOURCE";

    /** Value column name. */
    public static final String VALUE = "VALUE";

    /** Value end column name. */
    public static final String VALUE_END = "VALUE_END";

    /** Value start column name. */
    public static final String VALUE_START = "VALUE_START";

    /** Version column name. */
    public static final String VERSION = "VERSION";

    /** Disallow instantiation. */
    private ColumnNames()
    {
    }
}
